mapper.select:
SELECT *
FROM todocard
WHERE cid = #{cid} AND is_deleted = 0;

mapper.update:
UPDATE todocard
SET title = #{title}, content = #{content}
WHERE cid = #{cid} AND is_deleted = 0;

UPDATE todocard
SET position = #{position}
WHERE cid = #{cid} AND is_deleted = 0;

UPDATE todocard
SET success = 1
WHERE cid = #{cid} AND is_deleted = 0;

mapper.delete:
UPDATE todocard
SET is_deleted = 1, position = 0
WHERE cid = #{cid} AND is_deleted = 0;

mapper.select:
SELECT cid
FROM todocard
WHERE cid = #{cid} AND is_deleted = 0;
