mapper.select:
SELECT *
FROM todolist
WHERE lid = #{lid} AND is_deleted = 0;

mapper.update:
UPDATE todolist
SET title = #{title}
WHERE lid = #{lid} AND is_deleted = 0;

UPDATE todolist
SET position = #{position}
WHERE lid = #{lid} AND is_deleted = 0;

mapper.delete:
UPDATE todolist
SET is_deleted = 1, position = 0
WHERE lid = #{lid} AND is_deleted = 0;

mapper.select:
SELECT lid
FROM todolist
WHERE lid = #{lid} AND is_deleted = 0;
