SET sex = #{sex}
WHERE uid = #{uid}

UPDATE user
SET phone_number = #{phone_number}
WHERE uid = #{uid}

UPDATE user
SET address = #{address}
WHERE uid = #{uid}

UPDATE user
SET salt = #{salt}
WHERE uid = #{uid}

mapper.select:
