mapper.update:
UPDATE user
SET age = #{age}, sex = #{sex}, phone_number = #{phone_number}, address = #{address}
WHERE uid = #{uid} AND is_deleted = 0;

mapper.select:
SELECT uid
FROM user
WHERE email = #{email} AND is_deleted = 0;

SELECT pw
FROM user
WHERE email = #{email} AND is_deleted = 0;

SELECT salt
FROM user
WHERE uid = #{uid} AND is_deleted = 0;

mapper.update:
UPDATE user
SET is_deleted = 1
WHERE uid = #{uid}
